import React from 'react';
import { Table } from 'antd';
import { Button } from 'antd';

const columns = [{
  title: 'Actions',
  key: 'actions',
  render: (text, record) => (
    <span>
      <Button style={{marginRight: 3}} ghost type="primary" icon="caret-up" onClick={() => this.props.handleMoveUp(this.props.index)}></Button>
      <Button style={{marginRight: 3}} ghost type="primary" icon="caret-down"></Button>
      <Button ghost type="danger" icon="close"></Button>
    </span>
  )
}, {
  title: 'Layer Name',
  dataIndex: 'layer',
  key: 'layer',
}, {
  title: 'Team ID 1',
  dataIndex: 'team1',
  key: 'team1',
}, {
  title: 'Team ID 2',
  key: 'team2',
  dataIndex: 'team2',
}, {
  title: 'Attacking Team',
  key: 'action'
}, {
  title: 'Warnings',
  key: 'warningMessage',
  dataIndex: 'warningMessage',
}];

const LayerTable = (props) => (
  <Table
    columns={columns}
    dataSource={props.layerRotation}
    rowKey={record => record.key}
    pagination={false}
    style={{width: 900}}
  ></Table>
);

export default LayerTable;
