import React from 'react';
import Dropdown from './dropdown'
import ColumnNames from './columnNames'
import Maps from '../mapData/maps'
import RotationItem from './rotationItem'

class RotationMakerApp extends React.Component {
    state = { mapsRotation: [] };

    //handle adding map to rotation
    handleAddMap = (index) => {

        const mapRotationArray = [...this.state.mapsRotation]
        mapRotationArray.push(Maps[index]);

        //maprotationlogic
        this.checkLayersForIssues(mapRotationArray);
        this.setState({mapsRotation: mapRotationArray})
        
    }

    checkLayersForIssues = (layers) => {
        let mapRotationLength = layers.length;
        let newestIndex = mapRotationLength - 1;
        if (mapRotationLength > 1) {
            //faction check, see if same team next round.
            if (layers[newestIndex].team1 === layers[newestIndex - 1].team2 || layers[newestIndex].team2 === layers[newestIndex - 1].team1) 
            {
                layers[newestIndex].warningMessage = "Same Faction As Prev Layer"
            }

            else if ((layers[newestIndex].attackersId === 'team1' && layers[newestIndex - 1].attackersId === 'team2') || (layers[newestIndex].attackersId === 'team2' && layers[newestIndex - 1].attackersId === 'team1')) 
            {
                layers[newestIndex].warningMessage = "Same ATK/DEF Side As Prev Layer"
            }
        }

        if (mapRotationLength > 2) {
            if (layers[0].team1 === layers[newestIndex].team2 || layers[0].team2 === layers[newestIndex].team1) {
                layers[0].warningMessage = "Same Faction As Prev Layer"
            }

            else if ((layers[0].attackersId === 'team1' && layers[newestIndex].attackersId === 'team2') || (layers[0].attackersId === 'team2' && layers[newestIndex].attackersId === 'team1')) 
            {
                layers[0].warningMessage = "Same ATK/DEF Side As Prev Layer"
            }
        }
    }

    render = () => {

        return (
            <div>
            <h1>Squad Map Rotation Maker</h1>    
            <Dropdown addMap={this.handleAddMap}/> 
            <ColumnNames />
            {this.state.mapsRotation.map((map, index) => (
                <RotationItem key={map + index} layer={map} />
            ))}
            </div>
        )
    }
}


export default RotationMakerApp;
