import React from "react";
import { Card, Col, Row } from 'antd';
import { Modal, Button } from 'antd';
const { Meta } = Card;

const LayerCard = (props) => (
  <Col key={props.layer.name}>
    <Card
      key={props.layer.name}
      style={{ width: 250, marginBottom: 10 }}
      actions={[<span onClick={() => this.showModal(layer)}>View Map</span>, <span onClick={() => props.addMap(layer)}>Add</span>]}
    >
      <Meta
        title={props.layer.name}
        description={(
          <Row justify="center">
            <Col span={8}>
              <img alt="team1Image" src={layer.team1Image} style={{ width: 50, heigth: 25 }}></img>
            </Col>
            <Col span={2}></Col>
            <Col span={4} >
              <span>VS.</span>
            </Col>
            <Col span={2}></Col>
            <Col span={8}>
              <img alt="team2Image" src={layer.team2Image} style={{ width: 50, heigth: 25 }}></img>
            </Col>
          </Row>
        )}
      />
    </Card>
    <Modal
      title={props.layerSelected.name}
      visible={props.visible}
      onOk={props.handleOk}
      onCancel={props.handleCancel}
      centered={true}
      width={700}
      footer={[
        <Button key="ok" type="primary" onClick={props.handleOk}>
          Close
            </Button>
      ]}
    >
      <p>Image As Of Squad Version: {props.layerSelected.imageVers}</p>
      <p>Reminder: RAAS layers have random capture points</p>
      <img alt="layer" style={{ width: 600, height: 600 }} src={props.layerSelected.layerImage}></img>
    </Modal>
  </Col>
);

export default LayerCard;
