import React from "react";
import { Card, Col, Row, Icon, Avatar } from 'antd';
import { Modal, Button } from 'antd';
const { Meta } = Card;

class LayerOptions extends React.Component {
  state = { visible: false }

  test = () => {
    console.log("Test")
  }

  showModal = () => {
    this.setState({
      visible: true,
    });
  }

  handleOk = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  }

  handleCancel = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  }

