            style={{ width: 300 }}
            actions={[<Icon type="picture" onClick={this.showModal} />, <Icon type="plus-square" />]}
          >
            <Meta
              title={layer.name}
              description={(
                <Row type="flex" justify="center">
                  <Col span={8}>
                    <img src={layer.team1Image} style={{ width: 50, heigth: 25 }}></img>
                  </Col>
                  <Col span={2}></Col>
                  <Col span={4} >
                    <span style={{textAlign: "center", margin: "0 15"}}>VS.</span>
                  </Col>
                  <Col span={2}></Col>
                  <Col span={8}>
                    <img src={layer.team2Image} style={{ width: 50, heigth: 25 }}></img>
                  </Col>
                </Row>
              )}
            />
          </Card>
          <Modal
            title={layer.name}
            visible={this.state.visible}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            footer={[
              <Button key="ok" type="primary" onClick={this.handleOk}>
                Close
            </Button>
