import React from 'react';
import { Table } from 'antd';
import { Button } from 'antd';

const columns = [{
  title: 'Actions',
  key: 'actions',
  render: (text, record) => (
    <span>
      <Button type="primary" ><span style={{fontSize: 20}}>↑</span></Button>
      <Button type="primary" ><span style={{fontSize: 20}}>↓</span></Button>
      <Button type="primary" ><span style={{fontSize: 20}}>x</span></Button>
    </span>
  )
}, {
  title: 'Layer Name',
  dataIndex: 'layer',
  key: 'layer',
}, {
  title: 'Team ID 1',
  dataIndex: 'team1',
  key: 'team1',
}, {
  title: 'Team ID 2',
  key: 'team2',
  dataIndex: 'team2',
}, {
  title: 'Attacking Team',
  key: 'action'
}, {
  title: 'Warnings',
  key: 'warningMessage',
  dataIndex: 'warningMessage',
}];

const LayerTable = (props) => (
  <Table
    columns={columns}
    dataSource={props.layerRotation}
    rowKey={record => record.key}
    style={{width: 900}}
  ></Table>
);

export default LayerTable;
