import React from 'react';
import { Table, Divider, Tag } from 'antd';
import { Button } from 'antd';

const columns = [{
  title: 'Actions',
  key: 'actions',
  render: (text, record) => {
    <span><Button style={{marginLeft: 5 }} type="primary" >Add</Button></span>
  }
}, {
  title: 'Layer Name',
  dataIndex: 'layer',
  key: 'layer',
}, {
  title: 'Team ID 1',
  dataIndex: 'team1',
  key: 'team1',
}, {
  title: 'Team ID 2',
  key: 'team2',
  dataIndex: 'team2',
}, {
  title: 'Attacking Team',
  key: 'action'
}, {
  title: 'Warnings',
  key: 'warningMessage',
  dataIndex: 'warningMessage',
}];

const LayerTable = (props) => (
    <Table 
    columns={columns}
    dataSource={props.layerRotation}
    rowKey={record => record.key}
    ></Table>
);

export default LayerTable;
