import React from "react";
import { Card, Col, Row } from 'antd';
import { Modal, Button } from 'antd';
const { Meta } = Card;
import LayerCard from "./LayerCard"
class LayerOptions extends React.Component {
  state = { visible: false, layerSelected: {} }

  test = () => {
    console.log("Test")
  }

  showModal = (layer) => {
    console.log(layer)
    this.setState({
      visible: true,
      layerSelected: layer
    });
  }

  handleOk = (e) => {

    this.setState({
      visible: false,
    });
  }

  handleCancel = (e) => {

    this.setState({
      visible: false,
    });
  }

  render() {
    return (
      <Row type="flex" justify="center" gutter={12}>
        {this.props.layers.map(layer => (
          <LayerCard 
            layer={layer}
            addMap={this.props.addMap}
            layerSelected={this.state.layerSelected}
          />
        ))}
      </Row>
    )
  }

}


export default LayerOptions;
